import React, { Component } from "react";
import ResultList from "./ResultList";
import SearchForm from "./SearchForm";
import API from "../utils/API";

class SearchResultContainer extends Component {
    constructor(props) {
        super(props);
        this.state = {
            employees: [],
            search: ""
        };
    }

    componentDidMount() {
        API.getData()
            .then(response => {
                this.setState({ employees: response.data.employees })
            }).catch(err => console.log(err));
    }

    handleInputChange = (event) => {
        this.setState({ search: event.target.value })
    }


    searchEmployee(term) {
        return function (x) {
            return x.name.toLowercase().includes(term.toLowercase()) || !term;
        }
    }
    // handleFormSubmit = event => {
    //     event.preventDefault();
    //     this.setState({
    //         employees: ""
    //     })
    // }

    render() {

        // let filterEmployees = this.state.employees.filter((employee) => {
        //     return employee.name.toLowerCase().includes(this.state.search.toLowerCase())
        // })

        return (
            <div>
                <SearchForm
                    handleInputChange={this.handleInputChange} />
                <ResultList employees={this.state.employees} />
            </div>
        );
    }
}

export default SearchResultContainer;
